mapper.select:
SELECT * FROM FIND JOIN FINDIMG USING(FINDNO) JOIN MEMBER
USING(MEMBERNO) WHERE IMGSTATUS = 'M' AND STATUS='N'

	AND CATE=#{cate}

select
count(*) from member join find using(memberNo) where memberNo =
#{memberNo}

mapper.update:
UPDATE FIND SET
CATE=#{cate}, TITLE=#{title}, CONTENT=#{content}, ADDRESS=#{address}
WHERE FINDNO = #{findNo}
