mapper.select:
SELECT * FROM PLACE JOIN PLACEIMG USING(PLACENO) JOIN MEMBER
USING(MEMBERNO) WHERE TYPE='M' AND STATUS ='Y'

	AND CATEGORY=#{category}


	AND PLACENO IN (SELECT PLACENO FROM PLACE LEFT JOIN HASHTAG ON
	POSTNO=PLACENO WHERE HASHCONTENT=#{hashtag})


   AND PLACENO IN (SELECT PLACENO FROM PLACE LEFT JOIN HASHTAG ON POSTNO=PLACENO JOIN PLACEIMG USING(PLACENO) WHERE REGEXP_LIKE(TITLE,
   #{word}
   ) OR  REGEXP_LIKE(HASHCONTENT,
   #{word}
   )OR  REGEXP_LIKE(EXPLANATION,
   #{word}
   )OR  REGEXP_LIKE(CATEGORY,
   #{word}
   )OR  REGEXP_LIKE(CONTENT,
   #{word}
   ))

ORDER BY PLACEDATE DESC

SELECT COUNT(*) FROM PLACE WHERE STATUS ='Y'

	AND CATEGORY=#{category}


	AND PLACENO IN (SELECT PLACENO FROM PLACE LEFT JOIN HASHTAG ON
	POSTNO=PLACENO WHERE HASHCONTENT=#{hashtag})


   AND PLACENO IN (SELECT PLACENO FROM PLACE LEFT JOIN HASHTAG ON POSTNO=PLACENO JOIN PLACEIMG USING(PLACENO) WHERE REGEXP_LIKE(TITLE,
   #{word}
   ) OR  REGEXP_LIKE(HASHCONTENT,
   #{word}
   )OR  REGEXP_LIKE(EXPLANATION,
   #{word}
   )OR  REGEXP_LIKE(CATEGORY,
   #{word}
   )OR  REGEXP_LIKE(CONTENT,
   #{word}
   ))
